import React, { useState, useEffect, Component } from 'react';
import { storeProducts, detailProduct } from './data'
export const ProductContext = React.createContext()

const ProductConsumer = ProductContext.Consumer
export { ProductProvider, ProductConsumer }
export default class ProductProvider extends Component
{


  state={

    products: [],
    detailProd: detailProduct,
    cart: storeProducts,
    modalOpen: false,
    modalProduct: detailProduct,
    cartSubTotal: 0,
    cartTax: 0,
    cartTotal: 0
  }






   handleDetail = () => {
    console.log('hello from detail')
    const product = getItem()
    setProd({ ...prod, detailProduct: product })
  }

   addToCart = id => {
    let tempProducts = [...prod.products]
    const index = tempProducts.indexOf(getItem(id))
    const product = tempProducts[index]
    product.inCart = true
    product.count = 1
    const price = product.price
    product.total = price

    this.setState(

      ()=>{
        return { products:tempProducts,cart:[...this.state.products,product]


      }},
    )








  }

   getItem = (id) => {
    const product = prod.products.find(item => item.id == id)
    return product
  }
   setProducts = () => {
    let tempProducts = []
    storeProducts.forEach(item => {
      const singleItem = { ...item }
      tempProducts = [...tempProducts, singleItem]
    })
    setProd({ products: tempProducts })
  }
   closeModal = () => {
    setProd({ ...prod, modalOpen: false })
  }
   openModal = id => {
    const product = getItem(id)
    setProd({ ...prod, modalProduct: product, modalOpen: true })
  }
   increment = id => {
    let tempCart = [...prod.cart]
    let selectProduct = tempCart.find(x => x.id === id)
    const index = selectProduct.indexOf(selectProduct)
    const product = tempCart[index]
    product.count += 1
    product.total = product.count * product.price
    setProd({ ...prod, cart: [...tempCart] })
    addTotals()
  }

   decrement = id => {
    let tempCart = [...prod.cart]
    let selectProduct = tempCart.find(x => x.id === id)
    const index = selectProduct.indexOf(selectProduct)
    const product = tempCart[index]
    product.count -= 1
    if (product.count <= 0) removeItem(id)
    else {
      product.total = product.count * product.price
    }
    setProd({ ...prod, cart: [...tempCart] })
    addTotals()
  }

   removeItem = id => {
    let tempProducts = [...prod.products]
    let tempCart = [...prod.cart]
    tempCart = tempCart.filter(x => {
      return (x.id !== id)
    })
    const index = tempProducts.indexOf(getItem(id))
    let removedProduct = tempProducts[index]
    removedProduct.inCart = false
    removedProduct.count = 0
    removedProduct.total = 0
    setProd({ ...prod, cart: tempCart, products: tempProducts })
  }
   clearCart = () => {
    //setProd({ ...prod, cart: [] })
    setProducts()
    addTotals()
  }
   addTotals = () => {
    let subTotal = 0
    prod.cart.map(x => {
      return (subTotal += x.total)
    })
    const tempTax = subTotal * 0.1
    const tax = parseFloat(tempTax.toFixed(2))
    const total = subTotal + tax
    //setProd({ ...prod, cartSubTotal: subTotal, cartTax: tax, cartTotal: total })
  }
  render(){
  return (
    <>
      <ProductContext.Provider
        value={{
          ...state,
          handleDetail: this.handleDetail,
          addToCart: this.addToCart,
          openModal: this.openModal,
          closeModal: this.closeModal,
          increment: this.increment,
          decrement: this.decrement,
          removeItem: this.removeItem
        }}
      >
        {props.children}
      </ProductContext.Provider>
    </>
  )

}

}
